import React, { useState } from 'react';

import styled from 'styled-components';
import DictPage from './DictPage';
import DictList from './DictList';

import { firestore } from './firebase';

// 함수형 리덕스는 =
// useSelect(리덕스에 있는 상태값 가져오기), useDispatch(리덕스 스토어에 상태 넣기)

const App = () => {
	const [list, setList] = useState(true);

  React.useEffect(()=> { 
    const dict = firestore.collection("dict");

    dict.doc("dict_item1").get().then((doc) => {
      console.log(doc);
      console.log(doc.data());
      console.log(doc.id);
    });
  },[])

	const addBtn = () => {
		setList(!list);
	};
	return (
		<Container>
			{list ? <DictList setList={setList} /> : <DictPage />}
			<Plusbtn onClick={addBtn}>+</Plusbtn>
		</Container>
	);
};

const Plusbtn = styled.button`
	color: white;
	background-color: #4b3d37;
	border-radius: 100px;
	margin-left: 215px;
	font-size: 50px;
	font-weight: 650;
	border: none;
	width: 60px;
	height: 60px;
`;
const Container = styled.div`
	max-width: 280px;
	min-height: 80vh;
	background-color: #967a6e;
	padding: 16px;
	margin: 20px auto;
	border-radius: 5px;
	border: 1px solid #ddd;
`;

export default App;
